import React, { Component } from "react";
import { Row, Col, Container } from "react-bootstrap";
import Hasil from "./component/Hasil";
import ListCategories from "./component/ListCategories";
import NavbarComponent from "./component/NavbarComponent";
import { API_URL } from "./utils/constant";
import axios from "axios";
import {Link, Route, Switch, useLocation} from 'react-router-dom'

import Search from "./component/Search";

export default class App extends Component {
  constructor(props) {
    super(props);

    this.state = {
      menus: [],
      categoriygdipilih: "Makanan",
    };
  }

  componentDidMount() {
    axios
      .get(API_URL + "products?category.nama=" + this.state.categoriygdipilih)
      .then((res) => {
        const menus = res.data;
        this.setState({ menus });
      })
      .catch((error) => {
        console.log(error);
      });
  }
  changeCategory = (value) => {
    this.setState({
      categoriygdipilih: value,
      menus: [],
    });
    axios
      .get(API_URL + "products?category.nama=" + value)
      .then((res) => {
        const menus = res.data;
        this.setState({ menus });
      })
      .catch((error) => {
        console.log(error);
      });
  };

  render() {
    const { menus, categoriygdipilih } = this.state;
    return (
      <div className="App">
        <NavbarComponent />
        <div className="mt-3 mb-4">
          <Container fluid>
            <Row>
              <Col>
                <ListCategories
                  changeCategory={this.changeCategory}
                  categoriygdipilih={categoriygdipilih}
                />

              </Col>
            </Row>
          </Container>
        </div>
        <div className="mt-3 mb-4">
            <Container>
            <Row>
                <Col>
                <h4 className="mt-5">
                  <strong>daftar prodak</strong>
                </h4>
                <hr />
                </Col>
            </Row>
            </Container>
           <div>

           </div>

            <Link to="/detailProduct"><Search   details={menus} /></Link>  


        </div>

        <div className="mt-3 mb-4">
          <Container>
            <Row>
              <Col>
                <Hasil />
              </Col>
            </Row>
          </Container>
        </div>
      </div>



    );
  }
}
