// src/components/Search.js

import React, { useState } from "react";

import SearchList from "./SearchList";
import { Col } from "react-bootstrap";

function Search({ details }) {
  const [searchField, setSearchField] = useState("");

  const menu = details.filter((menu) => {
    return (
      menu.nama.toLowerCase().includes(searchField.toLowerCase()) ||
      menu.category.nama.toLowerCase().includes(searchField.toLowerCase())
    );
  });

  const handleChange = (e) => {
    setSearchField(e.target.value);
  };

  function searchList() {
    return <SearchList menus={menu} />;
  }

  return (
    <Col md={3} xs={6} className="mb-4">
    
  
    <section className="garamond">
      <div className="navy georgia ma0 grow">
        
      </div>
      <Form className="d-flex">
      <FormControl
        type="search"
        placeholder="Search"
        className="mr-2"
        aria-label="Search"
      />
      <Button variant="outline-success">Search</Button>
    </Form>
     
        {searchList()}
      
    </section>
    </Col>
    
  );
}

export default Search;
